import React from "react";
import { useState, useEffect } from "react";

const placeholders = [
  "Cabin",
  "House",
  "Flat",
  "Condo",
  "Austin, TX",
  "San Francisco, CA",
];

export default function Filter(props) {
  const [search, setSearch] = useState("");
  let properties = props.properties;

  const handleFilterChange = (e) => {
    setSearch(e.target.value);
  };

  useEffect(() => {
    if (search !== "") {
      console.log(`Searching for ${search}...`);
      console.log(properties);
      setProperties(
        properties
          .filter((property) => {
            property.description
              .toString()
              .toLowerCase()
              .includes(search.toLowerCase());
          })
          .map((property) => {
            console.log(property);
          })
      );
      console.log(properties);
    }
  }, [search]);

  // let handleInputChange = (e) => {
  //   let { name, value } = e.target;
  //   this.setState({
  //     ...this.state,
  //     inputs: {
  //       [name]: value,
  //     },
  //   });
  // };
  // let resetInputFields = () => {
  //   this.setState({ inputs: {} });
  // };

  return (
    <form
      onChange={(e) => handleFilterChange}
      class="content-center w-full max-w-lg"
    >
      <div class="flex items-center border-b border-teal-500 py-2 mb-2">
        <input
          name="filter"
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          // value={this.state.inputs.filterText}
          // onChange={handleInputChange}
          class="appearance-none bg-transparent border-none w-full text-gray-700 mr-3 py-1 px-2 leading-tight focus:outline-none"
          type="text"
          placeholder={
            placeholders[Math.floor(Math.random() * placeholders.length)]
          }
          aria-label="filter"
        />
        <button
          class="flex-shrink-0 bg-teal-500 hover:bg-teal-700 border-teal-500 hover:border-teal-700 text-sm border-4 py-1 px-2 rounded"
          type="submit"
        >
          Filter
        </button>
        <button
          class="flex-shrink-0 border-transparent border-4 text-teal-500 hover:text-teal-800 text-sm py-1 px-2 rounded"
          type="button"
          onclick={() => setSearch("")}
        >
          Clear
        </button>
      </div>
    </form>
  );
}
